require(['gitbook', 'jQuery'], function(gitbook, $) {
    var init = function () {
        var myTitle = gitbook.state.config.pluginsConfig['page-title'] || 'API Book';
        var myProject = gitbook.state.config.pluginsConfig['project-name'] || 'okex';
        $('title').text(myTitle);
        $('body').addClass(myProject);
    };
    gitbook.events.bind('page.change', function() {
        init();
    });
});
